UPDATE products
SET image_url = CONCAT('https://via.placeholder.com/150?text=', product_name);

WITH NumberedProducts AS (
    SELECT id_product, product_name, ROW_NUMBER() OVER () AS row_count
    FROM products
)
UPDATE products
SET product_name = CONCAT(NumberedProducts.product_name, ' ', NumberedProducts.row_count)
FROM NumberedProducts
WHERE products.id_product = NumberedProducts.id_product;
